import React, { useState } from 'react';
import axios from 'axios';
import ConnectionForm from './components/ConnectionForm';
import DatabaseSelector from './components/DatabaseSelector';
import TableSelector from './components/TableSelector';
import ColumnClassifier from './components/ColumnClassifier';
import FakeTableViewer from './components/FakeTableViewer';
import './App.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

export default function App() {
  const [step, setStep] = useState(1);
  const [conn, setConn] = useState(null);
  const [databases, setDatabases] = useState([]);
  const [tables, setTables] = useState([]);
  const [columns, setColumns] = useState([]);
  const [fakeData, setFakeData] = useState([]);
  const [ctx, setCtx] = useState({}); // holds { database, table, classification }

  const restart = () => {
    setStep(1);
    setConn(null);
    setDatabases([]);
    setTables([]);
    setColumns([]);
    setFakeData([]);
    setCtx({});
  };

  const handleConnect = async (props) => {
    try {
      const res = await axios.post(`${API_URL}/api/connect`, props);
      setDatabases(res.data);
      setConn(props);
      setStep(2);
    } catch (e) {
      alert('Connection failed: ' + e.message);
    }
  };

  const handleSelectDb = async (db) => {
    try {
      const res = await axios.post(`${API_URL}/api/tables`, {
        ...conn,
        database: db,
      });
      setTables(res.data);
      setCtx((c) => ({ ...c, database: db }));
      setStep(3);
    } catch (e) {
      alert('Load tables failed: ' + e.message);
    }
  };

  const handleSelectTable = async (table) => {
    try {
      const res = await axios.post(`${API_URL}/api/classify`, {
        ...conn,
        database: ctx.database,
        table,
      });
      setColumns(res.data);
      setCtx((c) => ({ ...c, table }));
      setStep(4);
    } catch (e) {
      alert('Classify failed: ' + e.message);
    }
  };

  const handleClassificationComplete = async (classification) => {
    try {
      const res = await axios.post(`${API_URL}/api/fake`, {
        ...conn,
        database: ctx.database,
        table: ctx.table,
        columns: classification,
      });
      setFakeData(res.data);
      setCtx((c) => ({ ...c, classification }));
      setStep(5);
    } catch (e) {
      alert('Fake-data generation failed: ' + e.message);
    }
  };

  return (
    <div className="app-container">
      {step === 1 && <ConnectionForm onConnect={handleConnect} />}
      {step === 2 && (
        <DatabaseSelector
          databases={databases}
          onSelectDb={handleSelectDb}
          onBack={restart}
        />
      )}
      {step === 3 && (
        <TableSelector
          tables={tables}
          onSelectTable={handleSelectTable}
          onBack={() => setStep(2)}
        />
      )}
      {step === 4 && (
        <ColumnClassifier
          columns={columns}
          onComplete={handleClassificationComplete}
          onBack={() => setStep(3)}
        />
      )}
      {step === 5 && (
        <FakeTableViewer
          data={fakeData}
          onRegenerate={() =>
            handleClassificationComplete(ctx.classification)
          }
          onRestart={restart}
        />
      )}
    </div>
  );
}
