import React, { useState } from 'react';

const COLUMN_TYPES = [
  'customerName',
  'phoneNumber',
  'email',
  'balance',
  'accountNumber',
  'IBAN',
  'isCorporateCustomer',
  'customerNumber',
  'nationalID',
];

export default function ColumnClassifier({
  previewData,
  columns,
  onComplete,
  onBack,
}) {
  const [idx, setIdx] = useState(0);
  const [working, setWorking] = useState(columns.map(c => ({ ...c })));
  const [editing, setEditing] = useState(false);
  const curr = working[idx];

  const confirm = () => {
    if (idx + 1 === working.length) {
      onComplete(working);
    } else {
      setIdx(idx + 1);
      setEditing(false);
    }
  };

  const change = (val) => {
    working[idx].suggestion = val;
    setWorking([...working]);
    setEditing(false);
  };

  return (
    <div className="classifier">
      <h3>Table Preview</h3>
      <div style={{ overflowX: 'auto', marginBottom: '24px' }}>
        <table>
          <thead>
            <tr>
              {previewData[0] &&
                Object.keys(previewData[0]).map(col => (
                  <th key={col}>{col}</th>
                ))}
            </tr>
          </thead>
          <tbody>
            {previewData.map((row, i) => (
              <tr key={i}>
                {Object.keys(row).map(col => (
                  <td key={col}>{row[col]}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <h3>
        Classify Columns ({idx + 1} / {working.length})
      </h3>
      <button onClick={onBack}>Back</button>
      <p>
        <strong>Column:</strong> {curr.columnName}
      </p>
      <p>
        <strong>Suggested:</strong> {curr.suggestion}
      </p>

      {editing ? (
        <>
          <label htmlFor="edit">Override Type</label>
          <select
            id="edit"
            value={curr.suggestion}
            onChange={e => change(e.target.value)}
          >
            {COLUMN_TYPES.map(t => (
              <option key={t} value={t}>{t}</option>
            ))}
          </select>
        </>
      ) : (
        <button onClick={() => setEditing(true)}>✎ Edit</button>
      )}

      <button onClick={confirm}>✔ Confirm</button>
    </div>
  );
}
