import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function FakeTableViewer({ connection, db, table, columnSpecs }) {
  const [data, setData] = useState([]);

  useEffect(() => {
    async function fetchFake() {
      const payload = { connection, rowCount: 10, columns: columnSpecs.reduce(
        (acc, spec) => ({ ...acc, [spec.columnName]: spec }), {})
      };
      const res = await axios.post(`/api/${db}/${table}/fake`, payload);
      setData(res.data);
    }
    if (columnSpecs.length) fetchFake();
  }, [columnSpecs]);

  if (!data.length) return <div>Loading fake data...</div>;

  return (
    <table>
      <thead>
        <tr>
          {Object.keys(data[0]).map(col => <th key={col}>{col}</th>)}
        </tr>
      </thead>
      <tbody>
        {data.map((row, idx) => (
          <tr key={idx}>
            {Object.values(row).map((val, i) => <td key={i}>{val}</td>)}
          </tr>
        ))}
      </tbody>
    </table>
  );
}
