import React, { useState, useEffect } from 'react';

const COLUMN_TYPES = [
  'customerName',
  'phoneNumber',
  'email',
  'balance',
  'accountNumber',
  'IBAN',
  'isCorporateCustomer',
  'customerNumber',
  'nationalID',
  'address',
  'UNKNOWN',
];

export default function ColumnClassifier({
  previewData,    // actual rows, e.g. [{ col1:…, col2:… }, …]
  columns,        // suggestions: [{ columnName: 'col1', suggestion: 'CUSTOMER_NAME' }, …]
  onComplete,
  onBack,
}) {
  // 1) extract real SQL column keys
  const realKeys = previewData[0]
    ? Object.keys(previewData[0])
    : [];

  // 2) build “working” list zipping the real column name → suggested type
  const [working, setWorking] = useState([]);
  useEffect(() => {
    setWorking(
      realKeys.map((key, i) => ({
        columnName: key,                  // raw column name (col1, col2…)
        suggestion: columns[i]?.suggestion ?? 'UNKNOWN'
      }))
    );
  }, [previewData, columns]);

  const [idx, setIdx]       = useState(0);
  const [editing, setEditing] = useState(false);
  const curr = working[idx] || { columnName: '', suggestion: '' };

  // when user ✔ confirms
  const confirm = () => {
    if (idx + 1 === working.length) {
      onComplete(working);
    } else {
      setIdx(idx + 1);
      setEditing(false);
    }
  };

  // when user overrides suggestion
  const change = (val) => {
    const next = [...working];
    next[idx].suggestion = val;
    setWorking(next);
    setEditing(false);
  };

  return (
    <div className="classifier">
      {/* REAL TABLE PREVIEW */}
      <h3>Table Preview</h3>
      <div style={{ overflowX:'auto', marginBottom:24 }}>
        <table>
          <thead>
            <tr>
              {realKeys.map(k => (
                <th key={k}>{k}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {previewData.map((row, i) => (
              <tr key={i}>
                {realKeys.map(k => (
                  <td key={k}>{row[k]}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* CLASSIFICATION WIZARD */}
      <h3>
        Classify Columns ({idx + 1} / {working.length})
      </h3>
      <button onClick={onBack}>Back</button>

      <p>
        <strong>Column:</strong> {curr.columnName}
      </p>
      <p>
        <strong>Suggested type:</strong> {curr.suggestion}
      </p>

      {editing ? (
        <>
          <label htmlFor="edit">Override Type</label>
          <select
            id="edit"
            value={curr.suggestion}
            onChange={e => change(e.target.value)}
          >
            {COLUMN_TYPES.map(t => (
              <option key={t} value={t}>{t}</option>
            ))}
          </select>
        </>
      ) : (
        <button onClick={() => setEditing(true)}>✎ Edit</button>
      )}

      <button onClick={confirm}>✔ Confirm</button>
    </div>
  );
}
