import React, { useState } from 'react';
import axios from 'axios';
import ConnectionForm from './components/ConnectionForm';
import DatabaseSelector from './components/DatabaseSelector';
import TableSelector from './components/TableSelector';
import ColumnClassifier from './components/ColumnClassifier';
import FakeTableViewer from './components/FakeTableViewer';
import './App.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

export default function App() {
  const [step, setStep] = useState(1);
  const [conn, setConn] = useState(null);
  const [databases, setDatabases] = useState([]);
  const [tables, setTables] = useState([]);
  const [previewData, setPreviewData] = useState([]);
  const [columns, setColumns] = useState([]);
  const [fakeData, setFakeData] = useState([]);
  const [ctx, setCtx] = useState({}); // { database, table, totalRows, classification }

  const restart = () => {
    setStep(1);
    setConn(null);
    setDatabases([]);
    setTables([]);
    setPreviewData([]);
    setColumns([]);
    setFakeData([]);
    setCtx({});
  };

  const handleConnect = async (props) => {
    try {
      const res = await axios.post(
        `${API_URL}/api/connect/databases`,
        props
      );
      setDatabases(res.data);
      setConn(props);
      setStep(2);
    } catch (e) {
      alert('Connection failed: ' + e.message);
    }
  };

  const handleSelectDb = async (db) => {
    try {
      const res = await axios.post(
        `${API_URL}/api/${encodeURIComponent(db)}/tables`,
        conn
      );
      setTables(res.data);
      setCtx(c => ({ ...c, database: db }));
      setStep(3);
    } catch (e) {
      alert('Load tables failed: ' + e.message);
    }
  };

  const handleSelectTable = async (table) => {
    try {
      // 1) Preview
      const previewRes = await axios.post(
        `${API_URL}/api/${encodeURIComponent(ctx.database)}/tables/${encodeURIComponent(table)}/preview?limit=10`,
        conn
      );
      setPreviewData(previewRes.data);

      // 2) Count rows
      const countRes = await axios.post(
        `${API_URL}/api/${encodeURIComponent(ctx.database)}/tables/${encodeURIComponent(table)}/count`,
        conn
      );
      const totalRows = countRes.data;

      // 3) Classification suggestions
      const classifyRes = await axios.post(
        `${API_URL}/api/${encodeURIComponent(ctx.database)}/${encodeURIComponent(table)}/classify`,
        {
          connection: conn,
          sampleSize: 10,
          overrides: {}
        }
      );
      setColumns(classifyRes.data);

      setCtx(c => ({ ...c, table, totalRows }));
      setStep(4);
    } catch (e) {
      alert('Error loading preview or classification: ' + e.message);
    }
  };

  const handleClassificationComplete = async (classification) => {
    try {
      const res = await axios.post(
        `${API_URL}/api/${encodeURIComponent(ctx.database)}/${encodeURIComponent(ctx.table)}/fake`,
        {
          connection: conn,
          rowCount: ctx.totalRows,
          columns: classification.reduce((map, c) => {
            map[c.columnName] = { type: c.suggestion };
            return map;
          }, {})
        }
      );
      setFakeData(res.data);
      setCtx(c => ({ ...c, classification }));
      setStep(5);
    } catch (e) {
      alert('Fake-data generation failed: ' + e.message);
    }
  };

  return (
    <div className="app-container">
      {step === 1 && <ConnectionForm onConnect={handleConnect} />}
      {step === 2 && (
        <DatabaseSelector
          databases={databases}
          onSelectDb={handleSelectDb}
          onBack={restart}
        />
      )}
      {step === 3 && (
        <TableSelector
          tables={tables}
          onSelectTable={handleSelectTable}
          onBack={() => setStep(2)}
        />
      )}
      {step === 4 && (
        <ColumnClassifier
          previewData={previewData}
          columns={columns}
          onComplete={handleClassificationComplete}
          onBack={() => setStep(3)}
        />
      )}
      {step === 5 && (
        <FakeTableViewer
          data={fakeData}
          onRegenerate={() =>
            handleClassificationComplete(ctx.classification)
          }
          onRestart={restart}
        />
      )}
    </div>
  );
}
